import{_ as l,d as i}from"./app.1f59b63a.js";const e={},d=i('<h1 id="vim\u4F7F\u7528\u6559\u7A0B" tabindex="-1"><a class="header-anchor" href="#vim\u4F7F\u7528\u6559\u7A0B" aria-hidden="true">#</a> Vim\u4F7F\u7528\u6559\u7A0B</h1><blockquote><p>\u5728\u8FD9\u91CC\u4F7F\u7528\u7684\u662F<code>VsCode</code>\u5B89\u88C5\u7684<code>Vim</code>\u63D2\u4EF6\u4F5C\u4E3A\u5C55\u793A</p></blockquote><h2 id="vim-\u6709\u56DB\u4E2A\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#vim-\u6709\u56DB\u4E2A\u6A21\u5F0F" aria-hidden="true">#</a> VIM \u6709\u56DB\u4E2A\u6A21\u5F0F\uFF1A</h2><ul><li>\u6B63\u5E38\u6A21\u5F0F (Normal-mode)</li><li>\u63D2\u5165\u6A21\u5F0F (Insert-mode)</li><li>\u547D\u4EE4\u6A21\u5F0F (Command-mode)</li><li>\u53EF\u89C6\u6A21\u5F0F (Visual-mode)</li></ul><h2 id="\u6A21\u5F0F\u5207\u6362" tabindex="-1"><a class="header-anchor" href="#\u6A21\u5F0F\u5207\u6362" aria-hidden="true">#</a> \u6A21\u5F0F\u5207\u6362</h2><ul><li>ESC/<code>ctrl</code> + <code>[</code> --&gt; \u5207\u6362\u6A21\u5F0F</li></ul><h2 id="\u6587\u4EF6\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u6587\u4EF6\u64CD\u4F5C" aria-hidden="true">#</a> \u6587\u4EF6\u64CD\u4F5C</h2><ul><li>vim fileName1 fileName2 ... fileN--&gt; vim\u6253\u5F00\u6587\u4EF6</li><li>vim -On file1 file1 ... fileN --&gt; vim \u5DE6\u53F3\u5206\u5C4F\u6253\u5F00\u6587\u4EF6</li><li>vim -on file1 file2 ... fileN --&gt; vim \u4E0A\u4E0B\u5206\u5C4F\u6253\u5F00\u6587\u4EF6</li><li><code>:ls</code> --&gt; \u5C55\u793Avim\u76EE\u524D\u6253\u5F00\u7684\u6587</li><li><code>Ctrl</code> + <code>w s</code> --&gt; \u4E0A\u4E0B\u5207\u5272\u5F53\u524D\u6253\u5F00\u7684\u6587\u4EF6 <ul><li><code>Ctrl</code> + <code>w j</code> --&gt; \u5C06\u5149\u6807\u79FB\u52A8\u5230\u4E0B\u9762\u7684\u5206\u5C4F</li><li><code>Ctrl</code> + <code>w k</code> --&gt; \u5C06\u5149\u6807\u79FB\u52A8\u5230\u4E0A\u9762\u7684\u5206\u5C4F</li></ul></li><li><code>Ctrl</code> + <code>w v</code> --&gt; \u5DE6\u53F3\u5207\u5272\u5F53\u524D\u6253\u5F00\u7684\u6587\u4EF6 <ul><li><code>Ctrl</code> + <code>w h</code> --&gt; \u5C06\u5149\u6807\u79FB\u52A8\u5230\u5DE6\u8FB9\u7684\u5206\u5C4F</li><li><code>Ctrl</code> + <code>w l</code> --&gt; \u5C06\u5149\u6807\u79FB\u52A8\u5230\u53F3\u8FB9\u7684\u5206\u5C4F</li></ul></li><li><code>Ctrl</code> + <code>w c</code> --&gt; \u5173\u95ED\u5F53\u524D\u7684\u5206\u5C4F</li></ul><h2 id="\u57FA\u672C\u7684\u79FB\u52A8\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u7684\u79FB\u52A8\u64CD\u4F5C" aria-hidden="true">#</a> \u57FA\u672C\u7684\u79FB\u52A8\u64CD\u4F5C</h2><p>h,j,k,l --&gt; [\u5DE6, \u4E0B, \u4E0A, \u53F3]</p><h2 id="\u57FA\u672C\u7684\u7F16\u8F91\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u7684\u7F16\u8F91\u64CD\u4F5C" aria-hidden="true">#</a> \u57FA\u672C\u7684\u7F16\u8F91\u64CD\u4F5C</h2><ul><li>i --&gt; \u5149\u6807\u5904\u63D2\u5165\u5185\u5BB9</li><li>I --&gt; \u5149\u6807\u6240\u5728\u9996\u884C\u63D2\u5165\u5185\u5BB9</li><li>a --&gt; \u5149\u6807\u9009\u62E9\u5B57\u6BCD\u540E\u63D2\u5165\u5185\u5BB9</li><li>A --&gt; \u5149\u6807\u6240\u9009\u884C\u7684\u884C\u5C3E\u63D2\u5165\u5185\u5BB9</li><li>o --&gt; \u5149\u6807\u7684\u4E0B\u4E00\u884C\u884C\u9996\u63D2\u5165\u5185\u5BB9</li><li>O --&gt; \u5149\u6807\u7684\u4E0A\u4E00\u884C\u884C\u9996\u63D2\u5165\u5185\u5BB9</li><li>s --&gt; \u751F\u4EA7\u51FA\u5149\u6807\u5904\u7684\u5B57\u7B26\u7136\u540E\u63D2\u5165\u9700\u8981\u7684\u5185\u5BB9</li><li>S --&gt; \u5220\u9664\u5149\u6807\u6240\u5728\u884C\uFF0C\u5728\u5F53\u524D\u884C\u7684\u884C\u9996\u63D2\u5165\u5185\u5BB9</li><li>r --&gt; \u66FF\u6362\u5F53\u524D\u6240\u9009\u5185\u5BB9 <ul><li>R --&gt; \u66FF\u6362\u5149\u6807\u5F00\u59CB\u540E\u7684\u6240\u6709\u5B57\u7B26</li></ul></li><li>d --&gt; \u5220\u9664\u5F53\u524D\u6240\u9009\u5185\u5BB9 <ul><li>dw --&gt; \u5220\u9664\u7B2C\u4E00\u4E2A\u5355\u8BCD</li><li>d<code>n</code>w --&gt; \u5220\u9664\u7B2Cn\u4E2A\u5355\u8BCD</li><li>dd --&gt; \u5220\u9664\u4E00\u6574\u884C</li><li>ndd --&gt; \u5220\u9664\u5149\u6807\u5F00\u59CB\u7684\u7B2Cn\u884C</li><li>d$ --&gt; \u5220\u9664\u5149\u6807\u5230\u672C\u884C\u7684\u672B\u5C3E</li><li>dG --&gt; \u5220\u9664\u5149\u6807\u6240\u5728\u7684\u884C\u5230\u6587\u672C\u7ED3\u675F\u7684\u4F4D\u7F6E</li></ul></li><li>y --&gt; \u590D\u5236 <ul><li>yw --&gt; \u590D\u5236\u4E00\u4E2A\u5355\u8BCD</li><li>yy --&gt; \u590D\u5236\u4E00\u884C</li><li>yG --&gt; \u590D\u5236\u5149\u6807\u6240\u5728\u884C\u5230\u6587\u672C\u7684\u7ED3\u675F</li><li>y$ --&gt; \u590D\u5236\u5149\u6807\u5230\u672C\u884C\u7684\u7ED3\u5C3E</li></ul></li><li>p --&gt; \u7C98\u8D34 <ul><li>p --&gt; \u7C98\u8D34\u5230\u5149\u6807\u540E</li><li>P --&gt; \u7C98\u8D34\u5230\u5149\u6807\u524D</li></ul></li><li>x --&gt; \u5220\u9664\u5149\u6807\u9009\u62E9\u5B57\u7B26</li><li>~ --&gt; \u6539\u53D8\u5149\u6807\u4E0B\u5B57\u6BCD\u5927\u5C0F\u5199 <ul><li><code>n</code>~ -&gt; \u4ECE\u5149\u6807\u4F4D\u7F6E\u5F00\u59CB\u7684\u7B2Cn\u4E2A\u5B57\u6BCD\u7684\u5927\u5C0F\u5199</li></ul></li><li>g~ --&gt; \u6539\u53D8\u5F53\u524D\u884C\u7684\u5B57\u6BCD\u5927\u5C0F\u5199</li><li>gUU --&gt; \u5C06\u5F53\u524D\u884C\u7684\u5B57\u6BCD\u5168\u90E8\u6539\u6210\u5927\u5199</li><li>guu --&gt; \u5C06\u5F53\u524D\u884C\u7684\u5B57\u6BCD\u5168\u90E8\u6539\u6210\u5C0F\u5199</li></ul><h2 id="\u53EF\u89C6\u5316\u6A21\u5F0F-\u6587\u672C\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u53EF\u89C6\u5316\u6A21\u5F0F-\u6587\u672C\u9009\u62E9" aria-hidden="true">#</a> \u53EF\u89C6\u5316\u6A21\u5F0F(\u6587\u672C\u9009\u62E9)</h2><ul><li>v + h/j/k/l --&gt; \u9009\u62E9\u90E8\u5206\u5185\u5BB9</li><li>V + h/j/k/l --&gt; \u9009\u62E9\u90E8\u5206\u884C</li><li><code>Ctrl + v</code> + h/j/k/l --&gt; \u9009\u62E9\u90E8\u5206\u5217\u7684\u5185\u5BB9 <ul><li>I --&gt; \u524D\u9762\u8F93\u5165</li><li>A --&gt; \u540E\u9762\u8F93\u5165</li></ul></li></ul><h2 id="\u547D\u4EE4\u7684\u57FA\u672C\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u547D\u4EE4\u7684\u57FA\u672C\u64CD\u4F5C" aria-hidden="true">#</a> \u547D\u4EE4\u7684\u57FA\u672C\u64CD\u4F5C</h2><ul><li><code>/</code> + <code>search(\u641C\u7D22\u7684\u5B57\u7B26)</code> --&gt; \u641C\u7D22\u6307\u5B9A\u5185\u5BB9 <ul><li>n --&gt; \u8DF3\u8F6C\u81F3\u4E0B\u4E2A\u641C\u7D22\u8BCD</li><li>N --&gt; \u8DF3\u8F6C\u81F3\u4E0A\u4E2A\u641C\u7D22\u8BCD</li></ul></li><li><code>:n</code> --&gt; \u8DF3\u8F6C\u5230\u7B2Cn\u884C <ul><li><code>:n1,n2d</code> --&gt; \u5220\u9664\u7B2Cn1,n1\u884C</li></ul></li></ul><h2 id="vim-\u9000\u51FA\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#vim-\u9000\u51FA\u64CD\u4F5C" aria-hidden="true">#</a> Vim \u9000\u51FA\u64CD\u4F5C</h2><ul><li><code>:w</code> --&gt; \u4FDD\u5B58\u6587\u4EF6\u4FEE\u6539\uFF0C\u4E0D\u9000\u51FA\u6587\u4EF6</li><li><code>:w!</code> --&gt; \u5F3A\u5236\u4FDD\u5B58\u4F46\u4E0D\u9000\u51FA\u6587\u4EF6</li><li><code>:w file</code> --&gt; \u4FDD\u5B58\u5F53\u524D\u7684\u6587\u4EF6\u7684\u4FEE\u6539\u5230file\u6587\u4EF6\u4E2D</li><li><code>:q!</code> --&gt; \u9000\u51FA\u6587\u4EF6\uFF0C\u4E0D\u4FDD\u5B58\u5BF9\u6587\u4EF6\u7684\u4FEE\u6539</li><li><code>:qa!</code> --&gt; \u9000\u51FA\u6240\u6709\u6587\u4EF6\uFF0C\u64A4\u9500\u6240\u6709\u7684\u4FEE\u6539\u5185\u5BB9</li><li><code>:x</code> --&gt; \u9000\u51FA\u6587\u4EF6\u5E76\u4FDD\u5B58\u6587\u4EF6\u7684\u4FEE\u6539</li><li><code>:e!</code> --&gt; \u653E\u5F03\u6587\u4EF6\u7684\u6240\u6709\u4FEE\u6539\uFF0C\u6062\u590D\u5230\u4E0A\u6B21\u4FDD\u5B58\u7684\u4F4D\u7F6E</li><li><code>:saveas file</code> --&gt; \u53E6\u5B58\u4E3Afile</li></ul><h2 id="\u7FFB\u9875\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u7FFB\u9875\u64CD\u4F5C" aria-hidden="true">#</a> \u7FFB\u9875\u64CD\u4F5C</h2><ul><li><code>Ctrl</code> + <code>f</code> --&gt; \u67E5\u770B\u4E0B\u4E00\u9875\u7684\u5185\u5BB9</li><li><code>Ctrl</code> + <code>b</code> --&gt; \u67E5\u770B\u4E0A\u4E00\u9875\u7684\u5185\u5BB9</li></ul>',20);function t(c,o){return d}var r=l(e,[["render",t],["__file","Linux-Vim\u4F7F\u7528\u624B\u518C.html.vue"]]);export{r as default};
